import React from 'react';

const Certifications = () => {
    const certifications = [
        {
            title: 'Certified JavaScript Developer',
            institution: 'XYZ Institute',
            year: '2023',
            link: 'https://example.com/certification/javascript'
        },
        {
            title: 'React Developer Certification',
            institution: 'ABC Academy',
            year: '2022',
            link: 'https://example.com/certification/react'
        },
        {
            title: 'Full Stack Web Development',
            institution: 'Tech School',
            year: '2021',
            link: 'https://example.com/certification/fullstack'
        }
    ];

    return (
        <div className="certifications">
            <h2>Certifications</h2>
            <ul>
                {certifications.map((cert, index) => (
                    <li key={index}>
                        <a href={cert.link} target="_blank" rel="noopener noreferrer">
                            {cert.title} - {cert.institution} ({cert.year})
                        </a>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Certifications;
