import React, { useState } from 'react';

const Header = () => {
    const [menuOpen, setMenuOpen] = useState(false);

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };

    return (
        <header className="navbar">
            <div className="container">
                <a href="/" className="navbar-brand">GUNAM VARDHAN</a>
                <div className="menu-toggle" onClick={toggleMenu}>
                    <span className="toggle-icon">&#9776;</span>
                </div>
                <nav className={`nav-links ${menuOpen ? 'mobile-active' : ''}`}>
                    <a href="#certifications" onClick={() => setMenuOpen(false)}>Certifications</a>
                    <a href="#projects" onClick={() => setMenuOpen(false)}>Projects</a>
                    <a href="#experience" onClick={() => setMenuOpen(false)}>Experience</a>
                    <a href="#education" onClick={() => setMenuOpen(false)}>Education</a>
                    <a href="#resume" onClick={() => setMenuOpen(false)}>Resume</a>
                    <a href="#links" onClick={() => setMenuOpen(false)}>Links</a>
                </nav>
                {/* Add Icons Section */}
                <div className="navbar-icons">
                    <a href="https://www.linkedin.com/in/yourprofile" target="_blank" rel="noopener noreferrer" aria-label="LinkedIn">
                        <i className="fab fa-linkedin"></i>
                    </a>
                    <a href="https://github.com/yourprofile" target="_blank" rel="noopener noreferrer" aria-label="GitHub">
                        <i className="fab fa-github"></i>
                    </a>
                    <a href="mailto:your-email@example.com" aria-label="Email">
                        <i className="fas fa-envelope"></i>
                    </a>
                    <a href="tel:123-456-7890" aria-label="Phone">
                        <i className="fas fa-phone"></i>
                    </a>
                </div>
            </div>
        </header>
    );
};

export default Header;
