// filepath: /c:/Users/gunam/OneDrive/Desktop/PROJECTS/PORTFOLIO/portfolio-website/src/components/Resume.js
import React, { useEffect, useState } from 'react';
import personalInfo from '../personal/info.json';

const Resume = () => {
  const [info, setInfo] = useState(null);

  useEffect(() => {
    setInfo(personalInfo);
  }, []);

  if (!info) return <div>Loading...</div>;

  return (
    <div className="resume-container">
      <h1>{info.name}</h1>
      <p>Email: {info.email}</p>
      <p>Phone: {info.phone}</p>
      <p>Address: {info.address}</p>

      <h2>Experience</h2>
      {info.experience.map((exp, index) => (
        <div key={index}>
          <h3>{exp.company}</h3>
          <p>{exp.role}</p>
          <p>{exp.duration}</p>
          <p>{exp.description}</p>
        </div>
      ))}

      <h2>Education</h2>
      {info.education.map((edu, index) => (
        <div key={index}>
          <h3>{edu.institution}</h3>
          <p>{edu.degree}</p>
          <p>{edu.year}</p>
        </div>
      ))}

      <h2>Projects</h2>
      {info.projects.map((project, index) => (
        <div key={index}>
          <h3>{project.name}</h3>
          <p>{project.description}</p>
          <a href={project.link} target="_blank" rel="noopener noreferrer">View Project</a>
        </div>
      ))}

      <h2>Certifications</h2>
      {info.certifications.map((cert, index) => (
        <div key={index}>
          <h3>{cert.name}</h3>
          <p>{cert.issuer}</p>
          <p>{cert.year}</p>
        </div>
      ))}

      <iframe src="/resume.pdf" width="100%" height="500px" title="Resume Preview"></iframe>
      <a href="/resume.pdf" download="resume.pdf">Download Resume (PDF)</a>
    </div>
  );
};

export default Resume;
